import React from 'react';
import Link from 'next/link';

const Layout = ({ children }) => {
  const links = [
    {
      path: '/',
      name: 'Home'
    },
    {
      path: '/about',
      name: 'About'
    },
    {
      path: '/projects',
      name: 'Projects'
    }
  ];
  return (
    <>
      <nav>
        <ul>
          {links &&
            links.map(link => (
              <Link href={link.path}>
                <a>{link.name}</a>
              </Link>
            ))}
        </ul>
      </nav>
      <main>{children}</main>
      <footer>Built with ðŸ”¥ by Achraf ASH</footer>
    </>
  );
};

export default Layout;
